import React from "react";
import { Field, reduxForm } from "redux-form";

class StreamCreate extends React.Component {
  renderInput({ input, label, meta }) {
    console.log(meta); // Will contain validation errors

    return (
      <div className="field">
        <label>{label}</label>
        <input {...input} autoComplete="off" />
        <div>{meta.error}</div>
      </div>
    );
  }

  onSubmit(formValues) {
    // event.preventDefault(); // No longer needed.  redux-form automatically applies this behavior
    // Usually onSubmit(event) argument is what is passed.  Now with redux-form we get the <Field> input values as arguments

    console.log(formValues);
  }

  render() {
    // console.log(this.props);
    return (
      <form
        onSubmit={this.props.handleSubmit(this.onSubmit)} // Remember this.props.handleSubmit is provided by redux-form
        className="ui form"
      >
        <Field name="title" component={this.renderInput} label="Enter Title" />
        <Field
          name="description"
          component={this.renderInput}
          label="Enter Description"
        />
        <button className="ui button primary">Submit</button>
      </form>
    );
  }
}

// redux-form validation. Empty errors object indicate no errors. This function is wired to redux-form at export default.
const validate = (formValues) => {
  const errors = {};

  if (!formValues.title) {
    errors.title = "You must enter a title";
  }

  if (!formValues.description) {
    errors.description = "You must enter a description";
  }

  return errors;
};

export default reduxForm({
  form: "streamCreate", // a unique identifier for this form
  validate: validate,
})(StreamCreate);

/*
  // 1 Wire up <input> onChange and value vs shortform of spreading properties {...formProps}
  // Spreading properties is a JSX shortcut that takes all key/value pairs and add as properties to input element
  renderInput(formProps) {
    // console.log(formProps); // {input: {..}, meta: {..}}
    return (
      <input
        onChange={formProps.input.onChange}
        value={formProps.input.value}
      />
    );
  }

  // 2 Spread operator - These two are equivalent
  function App1() {
    return <Greeting firstName="Ben" lastName="Hector" />;
  }

  function App2() {
    const props = {firstName: 'Ben', lastName: 'Hector'};
    return <Greeting {...props} />;
  }


  // 3 Destructuring formProps.input further becomes renderInput({input})
