import React from "react";
import { Field, reduxForm } from "redux-form";

class StreamCreate extends React.Component {
  renderInput(formProps) {
    // console.log(formProps); // {input: {..}, meta: {..}}

    const test = <input {...formProps.input} />; // JSX shortcut that takes all key/value pairs and add as properties to input element
    console.log(test.props);
    return test;
  }

  render() {
    return (
      <form>
        <Field name="title" component={this.renderInput} />
        <Field name="description" component={this.renderInput} />
      </form>
    );
  }
}

export default reduxForm({
  form: "streamCreate", // a unique identifier for this form
})(StreamCreate);

/*
  // 1 Wire up <input> onChange and value vs shortform of spreading properties {...formProps}
  renderInput(formProps) {
    // console.log(formProps); // {input: {..}, meta: {..}}
    return (
      <input
        onChange={formProps.input.onChange}
        value={formProps.input.value}
      />
    );
  }

  // 2 Spread operator - These two are equivalent
  function App1() {
    return <Greeting firstName="Ben" lastName="Hector" />;
  }

  function App2() {
    const props = {firstName: 'Ben', lastName: 'Hector'};
    return <Greeting {...props} />;
