import React from "react";
import { Field, reduxForm } from "redux-form";

class StreamCreate extends React.Component {
  renderInput(formProps) {
    console.log({ ...formProps.input }); // {input: {..}, meta: {..}}
    return <input {...formProps.input} />; // JSX shortcut that takes all key/value pairs and add as properties to input element
  }

  render() {
    return (
      <form>
        <Field name="title" component={this.renderInput} />
        <Field name="description" component={this.renderInput} />
      </form>
    );
  }
}

export default reduxForm({
  form: "streamCreate", // a unique identifier for this form
})(StreamCreate);

